import React from "react";

const Apps = () => {
  const projects = [
    {
      name: "Vistara – Homestay Website",
      url: "https://vistara-lime.vercel.app",
      desc: "A clean and modern homestay booking website.",
    },
    {
      name: "BullEdge Trading",
      url: "https://bull-edge.vercel.app",
      desc: "A trading dashboard with charts, holdings, positions and more.",
    },
  ];

  const styles = {
    container: {
      padding: "40px",
      fontFamily: "Inter, sans-serif",
      color: "#222",
    },
    title: {
      fontSize: "32px",
      fontWeight: 700,
      marginBottom: "25px",
    },
    grid: {
      display: "grid",
      gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
      gap: "25px",
    },
    card: {
      padding: "25px",
      borderRadius: "14px",
      background: "#fff",
      boxShadow: "0 4px 20px rgba(0,0,0,0.06)",
      transition: "transform 0.2s ease, box-shadow 0.2s ease",
      cursor: "pointer",
    },
    cardHover: {
      transform: "translateY(-4px)",
      boxShadow: "0 8px 30px rgba(0,0,0,0.1)",
    },
    name: {
      fontSize: "20px",
      fontWeight: 600,
      marginBottom: "8px",
    },
    desc: {
      fontSize: "15px",
      opacity: 0.8,
      marginBottom: "12px",
    },
    link: {
      color: "#4f46e5",
      fontWeight: 600,
      textDecoration: "none",
    },
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>My Applications</h1>

      <div style={styles.grid}>
        {projects.map((p, idx) => (
          <a
            key={idx}
            href={p.url}
            target="_blank"
            rel="noopener noreferrer"
            style={{ textDecoration: "none" }}
          >
            <div
              style={styles.card}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = styles.cardHover.transform;
                e.currentTarget.style.boxShadow = styles.cardHover.boxShadow;
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = "none";
                e.currentTarget.style.boxShadow = styles.card.boxShadow;
              }}
            >
              <div style={styles.name}>{p.name}</div>
              <div style={styles.desc}>{p.desc}</div>
              <span style={styles.link}>Visit →</span>
            </div>
          </a>
        ))}
      </div>
    </div>
  );
};

export default Apps;
